import React from "react"
import { useEffect, useRef } from "react"
import { Message } from "../../../server/messages"
import { useRefManager } from "./RefMessagesManager"
import MessageItem from "./MessageItem"

type SearchResults = {
    id: string;
    messages: Message[];
    onMessageClick?: (id: number) => void;
}

const SearchResults: React.FC<SearchResults> = ({ id, messages, onMessageClick }) => {
    const {registerPanel, unregisterPanel } = useRefManager()
    const targetsRef = useRef<Record<number, HTMLElement>>({});

    useEffect(() => {
        registerPanel(id, {targetsRef})

        return () => {
            unregisterPanel(id)
        }
    }, [i])

    if (messages.length === 0) {
        return (
            <div>
                <h3>No results found.</h3>
            </div>
        )
    }

    return (
        <div>
            {messages.map((msg) => (
                <MessageItem
                user={msg.user}
                content={msg.content}
                timestamp={msg.timestamp}
                onClick={onMessageClick ? () => onMessageClick(msg.id) : undefined}
                targetRef={(el) => {
                    if (el) {
                      targetsRef.current[msg.id] = el;
                    }
                  }}
                />
            ))}
        </div>
    )
};

export default SearchResults;
