import React from "react"
import { useState } from "react"
import { messages } from "."
import { useRefManager } from "./RefMessagesManager"
import SearchInput from "./SearchInput"
import SearchResults from "./MessageBox"
import "../styles/highlight.css"

const ChannelMessages = () => {
    const [searchQuery, setSearchQuery] = useState("") // for what we are typing
    const [confirmedQuery, setConfirmedQuery] = useState("") // for what we are searching

    const handleSearch = () => {
        setConfirmedQuery(searchQuery)
    }

    const filteredMessages = confirmedQuery === "" ? [] : messages.filter((msg) =>
        msg.content.toLowerCase().includes(confirmedQuery.toLowerCase()))

    const { getTarget } = useRefManager()
    const scrollToMessage = (id: number) => {
        const target = getTarget("message", id)

        if (target) {
            target.scrollIntoView({ behavior: 'smooth', block: 'center' });
            target.classList.add('highlight')
            setTimeout(() => {
                target.classList.remove('highlight')
                target.classList.add('message-item')}, 5000)
        }
    }

    return (
        <div style= {{ display: 'flex'}}>
            <div style={{ width: '50%', padding: '10px', borderRight: '1px solid gray', height: '630px', overflow: 'scroll', overflowX: 'hidden' }}>
                <h2>Search</h2>
                <SearchInput searchQuery={searchQuery} onSearchChange={setSearchQuery} />
                <button onClick={handleSearch}>Find</button>
                <SearchResults id="search" messages={filteredMessages} onMessageClick={scrollToMessage}/>
            </div>

            <div style={{ width: '50%', padding: '20px', height: '630px', overflow: 'scroll', overflowX: 'hidden'  }}>
                <h2>Messages</h2>
                <SearchResults id="message" messages={messages}/>
            </div>
        </div>
    )
};

export default ChannelMessages;
