import React from "react"

type MessageItem = {
    user: string;
    content: string;
    timestamp: string;
    onClick?: () => void;
    targetRef: (el: HTMLDivElement | null) => void;
    highlightedWord?: string;
}

const MessageItem: React.FC<MessageItem> = ({ user, content, timestamp, onClick, targetRef, highlightedWord }) => {
    const formattedTimestamp = new Date(timestamp).toLocaleString();

    const highlight = () => {
        if(!highlightedWord) {
            return content;
        }

        const words = content.split(new RegExp(`(${highlight})`, 'gi'))

        return words.map((word, i) => {
            if (word.toLowerCase() === highlightedWord.toLowerCase()) {
                return <span key={i} style={{ backgroundColor: 'yellow' }}>{word}</span>
            }
            else {
                return word;
            }
        })
    }

    return (
        <div ref={targetRef} onClick={onClick} style={{ padding: '10px', borderBottom: '1px solid #ccc', cursor: onClick ? 'pointer' : 'default' }}>
            <h4 style={{ margin: '0 0 5px 0' }}>{user}</h4>
            <p style={{ margin: '0 0 5px 0' }}>{highlight()}</p>
            <small style={{ color: 'gray' }}>{formattedTimestamp}</small>
        </div>
    )
};

export default MessageItem;
